CREATE TABLE IF NOT EXISTS "Posts" ("id"   SERIAL , "userId" VARCHAR(255) NOT NULL, "post" VARCHAR(255) NOT NULL, "imageUrl" VARCHAR(255), "usersRead" INTEGER[] NOT NULL, "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL, "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL, PRIMARY KEY ("id"));

CREATE TABLE IF NOT EXISTS "Users" ("id"   SERIAL , "email" VARCHAR(255) NOT NULL, "password" VARCHAR(255) NOT NULL, "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL, "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL, PRIMARY KEY ("id"));